package org.examples.serverrivage;

import Impl.*;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;

//@Configuration  // DÉSACTIVÉ - DataInitializer gère l'initialisation
public class RivageInitConfig {
    //@Bean("gestionnaireInit")  // DÉSACTIVÉ
    public Gestionnaire gestionnaire() {
        Gestionnaire g = new Gestionnaire();

        // Images SVG encodées en base64 pour affichage direct dans le GUI
        String svgDouble1 = "data:image/svg+xml;base64,";
        String svgTriple = "data:image/svg+xml;base64,";
        String svgDouble2 = "data:image/svg+xml;base64,";

        Hotel h = new Hotel("Hôtel Rivage", new Adresse("France","Sète","Quai du Large",1,"",43.39,3.69), Categorie.HAUT_DE_GAMME, 4);
        h.addChambre(new Chambre(h, 101, 2, 120, svgDouble1));
        h.addChambre(new Chambre(h, 102, 3, 150, svgTriple));
        h.addChambre(new Chambre(h, 103, 2, 130, svgDouble2));
        //h.addAgence(new Agence("rivageAgency", 0.10));

        g.addHotel(h);
        return g;
    }
}
